INSERT INTO db1057313_alphahyd.oil (id, category_id, title, path, description, content_html, content_markdown, image, date_create, meta_title, meta_description, meta_keywords, active, sorting, deleted) VALUES (1, 0, 'Масло гидравлическое HLP', 'maslo_gidravlicheskoe_hlp', null, '<p>Масло гидравлическое HLP масло глубокой селективной очистки с многофункциональным пакетом присадок, улучшающими антиокислительные, антикоррозионные, противоизносные, депрессорные, деэмульгирующие, противопенные свойства. Эти масла предназначены для применения в гидросистемах промышленного оборудования, работающих при механических и термических нагрузках, оснащенных сервогидравлическими установками, системами пропорционального регулирования и фильтрующими элементами с тонкостью фильтрации 3-5 мкм (класс чистоты лучше класса 8 по ГОСТ17216).</p>
', 'Масло гидравлическое HLP масло глубокой селективной очистки с многофункциональным пакетом присадок, улучшающими антиокислительные, антикоррозионные, противоизносные, депрессорные, деэмульгирующие, противопенные свойства. Эти масла предназначены для применения в гидросистемах промышленного оборудования, работающих при механических и термических нагрузках, оснащенных сервогидравлическими установками, системами пропорционального регулирования и фильтрующими элементами с тонкостью фильтрации 3-5 мкм (класс чистоты лучше класса 8 по ГОСТ17216).', null, '2015-10-08 12:52:07', 'Масло гидравлическое HLP', 'Масло гидравлическое HLP масло глубокой селективной очистки с многофункциональным пакетом присадок, улучшающими антиокислительные, антикоррозионные, противоизносные, депрессорные, деэмульгирующие, противопенные свойства. ', '', 1, 0, 0);
INSERT INTO db1057313_alphahyd.oil (id, category_id, title, path, description, content_html, content_markdown, image, date_create, meta_title, meta_description, meta_keywords, active, sorting, deleted) VALUES (2, 0, 'Масло гидравлическое HVLP', 'maslo_gidravlicheskoe_hvlp', null, '<p>Масло гидравлическое HVLP предназначено для все сезонной эксплуатации в качестве рабочей жидкости в системах гидроприводов и гидроуправления строительной, дорожной, лесозаготовительной, подъемно-транспортной техники и других машинах и агрегатах, эксплуатируемых на открытом воздухе при температуре окружающей среды от минус 30 до плюс 60 °С.</p>
', 'Масло гидравлическое HVLP предназначено для все сезонной эксплуатации в качестве рабочей жидкости в системах гидроприводов и гидроуправления строительной, дорожной, лесозаготовительной, подъемно-транспортной техники и других машинах и агрегатах, эксплуатируемых на открытом воздухе при температуре окружающей среды от минус 30 до плюс 60 °С.', null, '2015-10-08 12:51:46', 'Масло гидравлическое HVLP', 'Масло гидравлическое HVLP предназначено для все сезонной эксплуатации в качестве рабочей жидкости в системах гидроприводов и гидроуправления строительной, дорожной, лесозаготовительной, подъемно-транспортной техники и других машинах и агрегатах, эксплуатируемых на открытом воздухе при температуре окружающей среды от минус 30 до плюс 60 °С.', '', 1, 0, 0);
