function initMap(){var map=new google.maps.Map(document.getElementById("map"),{zoom:17,center:{lat:59.9306,lng:30.484946}});var marker=new google.maps.Marker({map:map,place:{location:{lat:59.930348,lng:30.484946},query:"Alpha-Hydro, Санкт-Петербург, Россия"},attribution:{source:"Альфа Гидро. Гидравлика",webUrl:"http://www.alpha-hydro.com"}});var infoWindow=new google.maps.InfoWindow({content:"<h3>Альфа-Гидро</h3> Санкт-Петербург, ул. Кржижановского, д.12/1"});marker.addListener("click",function(){infoWindow.open(map,marker)})}